    /*
     * Start Button event initializes the game when clicked and checks 
     * that each player has a character and none of them are the same.
     */
    private void startButtonMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_startButtonMouseClicked
        // ensures each player has a character and none are the same
        if (p1Char.getText().equals("") || p3Char.getText().equals("") 
                || p2Char.getText().equals("")) {
            textArea.setText("Please Enter a character for all players");
        } else if ((p1Char.getText().equals(p2Char.getText()) 
                || (p2Char.getText().equals(p3Char.getText())) 
                || (p3Char.getText().equals(p1Char.getText())))) {
            textArea.setText("Characters cannot be the same.");

        } else {
            //initialize player characters
            p1 = p1Char.getText();
            p2 = p2Char.getText();
            p3 = p3Char.getText();

            p1Char.setEditable(false);
            p3Char.setEditable(false);
            p2Char.setEditable(false);

            pTurn = 1;

            textArea.setText("Player 1's Turn");

            startGame();
           

        }


    }//GEN-LAST:event_startButtonMouseClicked

    
