    /*
     * Uses checkRows, checkCols, and checkDiag functions to see if 
     * a player has won the game.
     * If so, returns true
    */
    private boolean checkWin() {
        // Temporary string to store the current player character.
        String pChar = new String();

        switch (pTurn) {
            case 1:
                pChar = p1;
                break;
            case 2:
                pChar = p2;
                break;
            case 3:
                pChar = p3;
                break;
            default:
                break;
        }

        return (checkRows(pChar)) || (checkCols(pChar)) || (checkDiag(pChar));

    }

    
    /*
     * First calls the initButtonArr function to fill the button array with
     * the current characters in the game grid. This is called every turn to 
     * fill the array with all the changes every time someone clicks a button.
     * Checks each diagonal of the grid for a win by using loops to create a
     * string for each diagonal line, then checking if that string contains
     * the sequence of 4 player characters.
    */
    private boolean checkDiag(String pChar) {

        initButtonArray();

        String dia1 = new String("");
        String dia2 = new String("");
        String dia3 = new String("");
        String dia4 = new String("");
        String dia5 = new String("");
        String dia6 = new String("");
        String dia7 = new String("");
        String dia8 = new String("");
        String dia9 = new String("");
        String dia10 = new String("");
        String dia11 = new String("");
        String dia12 = new String("");
        String dia13 = new String("");
        String dia14 = new String("");
        String dia15 = new String("");
        String dia16 = new String("");
        String dia17 = new String("");
        String dia18 = new String("");

        
        for (int i = 1; i <= 8; i++) {
            dia1 = dia1.concat(buttonArr[i][i]);
        }
        for (int i = 1; i <= 7; i++) {
            dia2 = dia2.concat(buttonArr[i + 1][i]);
            dia6 = dia6.concat(buttonArr[i][i + 1]);
        }
        for (int i = 1; i <= 6; i++) {
            dia3 = dia3.concat(buttonArr[i + 2][i]);
            dia7 = dia7.concat(buttonArr[i][i + 2]);
        }
        for (int i = 1; i <= 5; i++) {
            dia4 = dia4.concat(buttonArr[i + 3][i]);
            dia8 = dia8.concat(buttonArr[i][i + 3]);
        }
        for (int i = 1; i <= 4; i++) {
            dia5 = dia5.concat(buttonArr[i + 4][i]);
            dia9 = dia9.concat(buttonArr[i][i + 4]);
        }
        for (int i = 8, j = 1; i >= 1 && j <= 8; i--, j++) {
            dia10 = dia10.concat(buttonArr[j][i]);
        }
        for (int i = 7, j = 1; i >= 1 && j <= 7; i--, j++) {
            dia11 = dia11.concat(buttonArr[j][i]);
            dia15 = dia15.concat(buttonArr[j + 1][i + 1]);
        }
        for (int i = 6, j = 1; i >= 1 && j <= 6; i--, j++) {
            dia12 = dia12.concat(buttonArr[j][i]);
            dia16 = dia16.concat(buttonArr[j + 2][i + 2]);
        }
        for (int i = 5, j = 1; i >= 1 && j <= 5; i--, j++) {
            dia13 = dia13.concat(buttonArr[j][i]);
            dia17 = dia17.concat(buttonArr[j + 3][i + 3]);
        }
        for (int i = 4, j = 1; i >= 1 && j <= 4; i--, j++) {
            dia14 = dia14.concat(buttonArr[j][i]);
            dia18 = dia18.concat(buttonArr[j + 4][i + 4]);
        }

        return (dia1.contains(pChar + pChar + pChar + pChar))
                || (dia2.contains(pChar + pChar + pChar + pChar))
                || (dia3.contains(pChar + pChar + pChar + pChar))
                || (dia4.contains(pChar + pChar + pChar + pChar))
                || (dia5.contains(pChar + pChar + pChar + pChar))
                || (dia6.contains(pChar + pChar + pChar + pChar))
                || (dia7.contains(pChar + pChar + pChar + pChar))
                || (dia8.contains(pChar + pChar + pChar + pChar))
                || (dia9.contains(pChar + pChar + pChar + pChar))
                || (dia10.contains(pChar + pChar + pChar + pChar))
                || (dia11.contains(pChar + pChar + pChar + pChar))
                || (dia12.contains(pChar + pChar + pChar + pChar))
                || (dia13.contains(pChar + pChar + pChar + pChar))
                || (dia14.contains(pChar + pChar + pChar + pChar))
                || (dia15.contains(pChar + pChar + pChar + pChar))
                || (dia16.contains(pChar + pChar + pChar + pChar))
                || (dia17.contains(pChar + pChar + pChar + pChar))
                || (dia18.contains(pChar + pChar + pChar + pChar));
    }

